function openNav() {
    document.getElementById("mySidenav").style.width = "250px";
    document.getElementById("main").style.marginLeft = "250px";
  }
  
  function closeNav() {
    document.getElementById("mySidenav").style.width = "0";
    document.getElementById("main").style.marginLeft= "0";
  }
  var dropdown = document.getElementsByClassName("dropdown-btn");
  var i;
  
  for (i = 0; i < dropdown.length; i++) {
    dropdown[i].addEventListener("click", function() {
    this.classList.toggle("active");
    var dropdownContent = this.nextElementSibling;
    if (dropdownContent.style.display === "block") {
    dropdownContent.style.display = "none";
    } else {
    dropdownContent.style.display = "block";
    }
    });
  }
  
  function popup() {
      var popup =  document.getElementsByClassName("popup")[0];
      var sidebar =  document.getElementsByClassName("sidebar")[0];
      var navbar =  document.getElementsByClassName("nav-div")[0];
      var main =  document.getElementsByClassName("main")[0];
      var maintable =  document.getElementsByClassName("maintable")[0];
      if( popup.style.display == "none"){
          popup.style.display="block";
          sidebar.classList.add("blur");
          navbar.classList.add("blur");
          main.classList.add("blur");
    
      }else{
              popup.style.display="none";
              sidebar.classList.remove("blur");
              navbar.classList.remove("blur");
              main.classList.remove("blur");   
      }
  }
  
  
  $(document).ready(function(){
    $("#large-input").change( function() {
      var btnchxsave =  document.getElementsByClassName("btnchxsave")[0];
      btnchxsave.classList.add("popupbtnsave");
    });
  });